import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useLocation } from "react-router-dom";
import { setEmail, setPassword } from "../../features/inputSlice";
import { API_BASE_URL } from "../../utils/services/userService";

export default function SigninContent() {
  const location = useLocation();
  const email = useSelector((state) => state.input.email);
  const password = useSelector((state) => state.input.password);
  const [error, setError] = useState(null);
  const dispatch = useDispatch();
  const handleEmailChange = (event) => {
    const newEmail = event.target.value;
    dispatch(setEmail(newEmail));
  };
  const handlePasswordChange = (event) => {
    const newPassword = event.target.value;
    dispatch(setPassword(newPassword));
  };

  const handleSignin = async () => {
    try {
      const getTokenUser = async () => {
        const user = {
          email: email,
          password: password,
        };
        const response = await fetch(`${API_BASE_URL}login`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(user),
        });
        if (response.ok) {
          const data = await response.json();
          const token = data.token;
          console.log(token);
          return token;
        }
      };

    } catch (error) {
      setError("une erreur s'est produite.");
    }
  };

  return (
    <section className="sign-in-content">
      <i className="fa fa-user-circle sign-in-icon"></i>
      <h1>Sign In</h1>
      <form>
        <div className="input-wrapper">
          <label htmlFor="email">Email</label>
          <input
            type="text"
            id="email"
            value={email}
            onChange={handleEmailChange}
          />
        </div>
        <div className="input-wrapper">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </div>
        <div className="input-remember">
          <input type="checkbox" id="remember-me" />
          <label htmlFor="remember-me">Remember me</label>
        </div>
        <div className="sign-in-button">
          <Link
            to="/user/:id"
            className={location.pathname === "/user/:id" ? "active" : ""}
            onClick={handleSignin}
          >
            <p>Sign In</p>
          </Link>
          {error && <p className="error-message">{error}</p>}
        </div>
      </form>
    </section>
  );
}
