import { useDispatch } from "react-redux";
import { setFirstName } from "../../features/userProfileSlice";


export const API_BASE_URL = "http://localhost:3001/api/v1/user/";
const dispatch = useDispatch

export const loginUser = async (x, y) => {
    const user = {
        email: x,
        password: y,
    }
    const response = await fetch(`${API_BASE_URL}login`, {
        method: 'POST',
        headers: {
            'Content-Type': "application/json",
        },
        body: JSON.stringify(user),
    })
    if (response.ok) {
        const data = await response.json()
        const token = data.body.token
        const userProfile = async () => {
            const responseProfile = await fetch(`${API_BASE_URL}profile`, {
                method: 'POST',
                headers: {
                    Authorization: `Bearer${token}`,
                    'Content-Type': "application/json",
                }
            })
            const dataProfile = await responseProfile.json()
            console.log(dataProfile)
            const firstName = dataProfile.body.firstName
            const id = dataProfile.body.id
            const lastName = dataProfile.body.lastName
            const userName = dataProfile.body.userName
            dispatch(setFirstName(firstName))
            dispatch
        }
        userProfile()
        return token
    }
    else {
        throw new Error ("Echec de l'authentification")
    }
}
