import { setEmail, setPassword } from "../../features/inputSlice";

const API_BASE_URL = "http://localhost:3001/api/v1/user/";

export const getTokenUser = async () => {
  function getForms() {
    const user = { 'email': setEmail, 'password': setPassword };
    return user
  }
  const response = await fetch(`${API_BASE_URL}login`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(getForms()),
  });
  if (response.ok) {
    const data = await response.json();
    const token = data.token;
    return token;
  }
};
